sys_script_email.script:
(function runMailScript(/* GlideRecord */ current, /* TemplatePrinter */ template,
          /* Optional EmailOutbound */ email, /* Optional GlideRecord */ email_action,
          /* Optional GlideRecord */ event) {

          // Add your code here
	var righnow = new GlideDateTime();

	var needItTask = new GlideRecord('x_58872_needit_needit_task');
	needItTask.addQuery('due_date','<=', 'rightnow'); 
	needItTask.addQuery('current.assigned_to','assigned_to');
	needItTask.addQuery('state','!=', '3');
	needItTask.query();
	var count = needItTask.getRowCount();
	if(count>1)
		template.print('In addition to this task, you have '+ (count -1) + ' more overdue NeedIt Tasks.');

})(current, template, email, email_action, event);
