sys_script_include.script:
var NeedItUtils = Class.create();
NeedItUtils.prototype = {
    initialize: function() {
    },

	isDatePast : function(StrDate){
	var	StartDate = new GlideDateTime(StrDate);
	var CurrDate = new GlideDateTime();
		if(StartDate.before(CurrDate))
		{return false;

		}
		else
			return true;

	},

	isDateToday : function(StrDate){
	var	StartDate = new GlideDateTime(StrDate);
	var CurrDate = new GlideDateTime();
	var today = CurrDate.getLocalDate();
	var isToday = StartDate.getLocalDate();
	if (today.compareTo(isToday)== 0)
		{return false;

		}
		else
			return true;


	},

    type: 'NeedItUtils'
};
