sys_ws_operation.operation_script:
(function process(/*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {

    // implement resource here
var niuser = request.pathParams.user_name;
var user = new GlideRecord('sys_user');
user.get( niuser);

var ni = new GlideAggregate('x_58872_needit_needit');
ni.addAggregate('COUNT');
//ni.addActiveQuery();
ni.addQuery('u_requested_for', user.sys_id);
ni.groupBy('u_request_type');
ni.query();

var niCount = 0;
var niUserCount = 0;
var niRecs = [];

while (ni.next())
{
	niCount = ni.getAggregate('COUNT');
	var niReqType = ni.u_request_type.getDisplayValue();
	niRecs.push({'requestType': niReqType, 'count' : niCount});
	niUserCount = parseInt(niUserCount) + parseInt(niCount);
}

var body ={};
body.totalUserNiRecs= niUserCount;
body.catCounts = niRecs;
body.user = {'User Name' : user.user_name, 'User ID' : user.sys_id};

response.setBody(body);



})(request, response);
